package loops;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 10; i++) {
			System.out.print(i + " ");
		}
		System.out.println("\n"); // new line or gap between upper and lower print

		for (int i = 10; i >= 1; i--) {
			System.out.println(i);
		}
	}

}
